	public String getGradeString() {
		if(null==grade || 0==grade){
			return "0";
		}
		if(grade<=10000){
			return grade.toString();
		}
		if(grade>10000){
			int n=grade/10000;
			gradeString=n+"w+";
		}
		return gradeString;
	}

