mapper.select:
select 

  distinct 

 id_ id,user_name_ userName,account_name_ accountName , islocked_ islocked , status_ status,nickname_ nickname,sex_ sex,mobile_ mobile,
 email_ email,vip_ vip,level_ level,fans_ fans,attention_ attention,topic_count_ topicCount,entry_count_ entryCount,type_ type from sys_account 




  order by ${orderByClause} 

limit #{firstResult} , #{maxResults}
